import React, { useEffect, useState } from "react";
import logonobr from '../logo no-background.png';
import { Link } from "react-router-dom";
import axios from "axios";

const Payment = () => {
    const [Phone, setPhone] = useState();
    const [Products, setProducts] = useState([]);
    const [value, setValue] = useState({
        UserName: '',
        UserPhone: '',
        Ma_SP: '',
        Gia_SP: '',
        So_luong: '',
        Tong_TT: '',
        Hinh_anh: '',
    });

    const formatPrice = (price) => {
        return (price || 0).toLocaleString("vi-VN");
    };

    useEffect(() => {
        const fetchData = async (Phone) => {
            try {
                const response = await axios.get(`http://localhost:3308/getcart/${Phone}`,);
                setProducts(response.data);
            } catch (error) {
                console.error('Error fetching data', error);
            }
        };

        const phoneformlocalstorage = localStorage.getItem('userInfo');
        if (phoneformlocalstorage) {
            const userInfo = JSON.parse(phoneformlocalstorage);
            setPhone(userInfo.phone);
            fetchData(userInfo.phone);
        }
    }, []);

    const CalculateTotal = (product) => {
        
    }

    return (
        <div className="container">
            <header>
                <div className="header">
                    <img src={logonobr} height="100" width="100" alt="" ></img>
                    <h1 className="pt-2">Tên trang web</h1>
                </div>
            </header>
            <section className="chuyentrang">
                <div className="container">
                    <div className="row">
                        <nav aria-label="breadcrumb">
                            <ol className="breadcrumb">
                                <li className="breadcrumb-item"><Link to={'/'} className="trangchu text-black">Trang chủ</Link></li>
                                <li className="breadcrumb-item active" aria-current="page">Thanh toán</li>
                            </ol>
                        </nav>
                    </div>
                </div>
            </section>

            <main>
                <h2 style={{ paddingLeft: 30 }}>Giỏ hàng của bạn</h2>
                <table className="table table-hover" style={{ textAlign: "center" }}>
                    <thead>
                        <tr>
                            <th>Hình ảnh</th>
                            <th>Tên sản phẩm</th>
                            <th>Giá</th>
                            <th>Số lượng</th>
                            <th>Thành tiền</th>
                        </tr>
                    </thead>
                    <tbody>
                        {Products.map(products => (
                            <tr key={products.Ma_SP}>
                                <td><img src={`http://localhost:3308/images/` + products.Hinh_anh} alt="Hình ảnh sản phẩm"></img></td>
                                <td>{products.Ma_SP}</td>
                                <td>{formatPrice(products.Gia_ban)} VNĐ</td>
                                <td>{products.So_luong}</td>
                                <td>{formatPrice(products.Tong_tien)} VNĐ</td>
                            </tr>
                        ))}
                    </tbody>
                    <tfoot>
                        <tr>
                            <td colSpan={4} style={{textAlign: "right"}}>Tổng tiền:</td>
                            <td id="total-price">500.000₫</td>
                        </tr>
                    </tfoot>
                </table>
                <div className="container my-5">
                    <h1>Thông tin thanh toán</h1>
                    <form action="#">
                        <div className="row">
                            <div className="col-md-8">
                                <div className="mb-3">
                                    <label htmlFor="name" className="form-label">Họ và tên:</label>
                                    <input type="text" className="form-control" id="name" required></input>
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="email" className="form-label">Email:</label>
                                    <input type="email" className="form-control" id="email" required></input>
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="phone" className="form-label">Số điện thoại:</label>
                                    <input type="tel" className="form-control" id="phone" required></input>
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="address" className="form-label">Nơi nhận:</label>
                                    <input type="add" className="form-control" id="address" required></input>
                                </div>
                            </div>
                            <h2>Phương thức thanh toán</h2>
                            <div className="form-check">
                                <input type="radio" className="form-check-input" name="payment_method" id="cod" value="cod" checked></input>
                                <label className="form-check-label" htmlFor="cod">Thanh toán khi nhận hàng (COD)</label>
                            </div>
                            <div className="form-check">
                                <input type="radio" className="form-check-input" name="payment_method" id="online" value="online"></input>
                                <label className="form-check-label" htmlFor="online">Thanh toán trực tuyến (momo)</label>
                            </div>
                            <hr></hr>
                            <div className="d-flex justify-content-end">
                                <a href="ChiTietDonHang.html" className="btn btn-success">Thanh toán</a>
                            </div>
                        </div>
                    </form>
                </div>
            </main>
            <hr></hr>
        </div>
    )
}

export default Payment;
