// ShoppingCart.js
import React, { useState, useEffect } from 'react';


const ShoppingCart = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [newProduct, setNewProduct] = useState({
    ten_sanpham: '',
    gia_ban: 0,
    soluong: 1, // Số lượng mặc định là 1 khi thêm sản phẩm mới
  });

  useEffect(() => {
    // Fetch cart data from server
    // Example: axios.get('/api/cart').then((response) => setCart(response.data));
    calculateTotal(); // Tính toán tổng giá trị khi danh sách giỏ hàng được cập nhật
  }, [cart]); // Thêm cart vào dependencies để useEffect được gọi khi cart thay đổi

  const calculateTotal = () => {
    let totalPrice = 0;
    cart.forEach((item) => {
      totalPrice += item.gia_ban * item.soluong;
    });
    setTotal(totalPrice);
  };

  const handleCheckout = () => {
    // Example: axios.post('/api/checkout', { ten_sanpham, gia_ban, soluong, thanhtien })
    // .then(() => {
    //   // Payment successful, update UI or redirect
    // })
    // .catch((error) => {
    //   // Handle payment error
    // });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  const addProductToCart = () => {
    setCart((prevCart) => [...prevCart, newProduct]);
    setNewProduct({
      ten_sanpham: 'abc',
      gia_ban: 0,
      soluong: 1,
    });
  };

  return (
    <div>
      <h2>Shopping Cart</h2>
      <ul>
        {cart.map((item, index) => (
          <li key={index}>
            {item.ten_sanpham} - {item.gia_ban} VND - Quantity: {item.soluong}
          </li>
        ))}
      </ul>
      <p>Total: {total} VND</p>

      {/* Form để thêm sản phẩm mới */}
      <div>
        <h3>Add New Product</h3>
        <label>Name:</label>
        <input type="text" name="ten_sanpham" value={newProduct.ten_sanpham} onChange={handleInputChange} />
        <br />
        <label>Price:</label>
        <input type="number" name="gia_ban" value={newProduct.gia_ban} onChange={handleInputChange} />
        <br />
        <label>Quantity:</label>
        <input type="number" name="soluong" value={newProduct.soluong} onChange={handleInputChange} />
        <br />
        <button onClick={addProductToCart}>Add to Cart</button>
      </div>

      <button onClick={handleCheckout}>Checkout</button>
    </div>
  );
};

export default ShoppingCart;
