// Alert.js
import React, { useEffect } from 'react';

const Alert = ({ show, type, message }) => {
  useEffect(() => {
    if (show) {
      const timeoutId = setTimeout(() => {
        // Ẩn alert sau 2 giây
        hideAlert();
      }, 2000);

      return () => clearTimeout(timeoutId);
    }
  }, [show]);

  const hideAlert = () => {
    // Gọi hàm từ parent để ẩn alert
    // Thực tế, bạn có thể sử dụng Redux hoặc Context để quản lý trạng thái toàn cục
    // và tránh việc truyền callback từ parent xuống
  };

  if (!show) return null;

  return (
    <div className={`alert alert-${type}`} role="alert">
      {message}
    </div>
  );
};

export default Alert;
