// ShoppingCart.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ShoppingCart = () => {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    // Fetch cart data from server
    // Example: axios.get('/api/cart').then((response) => setCart(response.data));
  }, []);

  const calculateTotal = () => {
    let totalPrice = 0;
    cart.forEach((item) => {
      totalPrice += item.gia_ban * item.soluong;
    });
    setTotal(totalPrice);
  };

  const handleCheckout = () => {
    // Example: axios.post('/api/checkout', { ten_sanpham, gia_ban, soluong, thanhtien })
    // .then(() => {
    //   // Payment successful, update UI or redirect
    // })
    // .catch((error) => {
    //   // Handle payment error
    // });
  };

  return (
    <div>
      <h2>Shopping Cart</h2>
      <ul>
        {cart.map((item) => (
          <li key={item.id}>
            {item.ten_sanpham} - {item.gia_ban} VND - Quantity: {item.soluong}
          </li>
        ))}
      </ul>
      <p>Total: {total} VND</p>
      <button onClick={handleCheckout}>Checkout</button>
    </div>
  );
};

export default ShoppingCart;
